#include &lt;lua.h&gt;
#include &lt;lualib.h&gt;
#include &lt;lauxlib.h&gt;

#include &lt;lua5.1/lua.h&gt;
#include &lt;lua5.1/lualib.h&gt;
#include &lt;lua5.1/lauxlib.h&gt;

修改auto/configure：
LUA_LIBS=&quot;-L${vi_cv_path_lua_pfx}/lib -llua&quot;

LUA_LIBS=&quot;-L${vi_cv_path_lua_pfx}/lib -llua5.1&quot;

編譯與安裝：
% make
% sudo make install

